import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API = 'http://localhost:5000';

function App() {
  const [tasks, setTasks] = useState([]);
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [carregando, setCarregando] = useState(false);

  useEffect(() => {
    carregarTarefas();
  }, []);

  const carregarTarefas = async () => {
    try {
      const res = await axios.get(`${API}/tasks`);
      setTasks(res.data);
      setError('');
    } catch (err) {
      setError('Erro ao carregar tarefas. Verifique se o backend está rodando.');
    }
  };

  const adicionarTarefa = async () => {
    if (description.trim() === '') return;

    setCarregando(true);
    try {
      const res = await axios.post(`${API}/tasks`, { description });
      setTasks([...tasks, res.data]);
      setDescription('');
      setMensagem('Tarefa adicionada com sucesso!');
      setTimeout(() => setMensagem(''), 3000);
    } catch (err) {
      setError('Erro ao adicionar tarefa.');
    } finally {
      setCarregando(false);
    }
  };

  const excluirTarefa = async (id) => {
    try {
      await axios.delete(`${API}/tasks/${id}`);
      setTasks(tasks.filter((task) => task.id !== id));
      setMensagem('Tarefa excluída!');
      setTimeout(() => setMensagem(''), 3000);
    } catch (err) {
      setError('Erro ao excluir tarefa.');
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Segoe UI', maxWidth: 700, margin: '0 auto' }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>Trabalho APAW</h1>

      <div style={{ marginBottom: '1rem', display: 'flex', gap: '0.5rem' }}>
        <input
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Digite a tarefa"
          style={{
            padding: '0.5rem',
            flex: 1,
            border: '1px solid #ccc',
            borderRadius: '4px'
          }}
        />
        <button
          onClick={adicionarTarefa}
          disabled={carregando}
          style={{
            padding: '0.5rem 1rem',
            backgroundColor: '#28a745',
            color: 'white',
            border: 'none',
            borderRadius: '4px',
            cursor: carregando ? 'not-allowed' : 'pointer'
          }}
        >
          {carregando ? 'Adicionando...' : 'Adicionar'}
        </button>
      </div>

      {mensagem && <p style={{ color: 'green' }}>{mensagem}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      <table style={{
        width: '100%',
        borderCollapse: 'collapse',
        boxShadow: '0 0 10px rgba(0,0,0,0.1)'
      }}>
        <thead>
          <tr style={{ backgroundColor: '#f0f0f0' }}>
            <th style={{ padding: '8px', border: '1px solid #ccc' }}>#</th>
            <th style={{ padding: '8px', border: '1px solid #ccc' }}>Descrição</th>
            <th style={{ padding: '8px', border: '1px solid #ccc' }}>Ações</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length === 0 ? (
            <tr>
              <td colSpan="3" style={{ textAlign: 'center', padding: '1rem' }}>
                Nenhuma tarefa cadastrada.
              </td>
            </tr>
          ) : (
            tasks.map((task, index) => (
              <tr key={task.id}>
                <td style={{ padding: '8px', border: '1px solid #ccc' }}>{index + 1}</td>
                <td style={{ padding: '8px', border: '1px solid #ccc' }}>{task.description}</td>
                <td style={{ padding: '8px', border: '1px solid #ccc' }}>
                  <button
                    onClick={() => excluirTarefa(task.id)}
                    style={{
                      backgroundColor: '#dc3545',
                      color: 'white',
                      border: 'none',
                      padding: '5px 10px',
                      borderRadius: '4px',
                      cursor: 'pointer'
                    }}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default App;
