import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API = 'http://localhost:5000';

function App() {
  const [tasks, setTasks] = useState([]);
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  // Carrega tarefas ao iniciar
  useEffect(() => {
    carregarTarefas();
  }, []);

  const carregarTarefas = async () => {
    try {
      const res = await axios.get(`${API}/tasks`);
      setTasks(res.data);
      setError('');
    } catch (err) {
      setError('Erro ao carregar tarefas. Verifique o backend.');
    }
  };

  const adicionarTarefa = async () => {
    if (description.trim() === '') return;

    try {
      const res = await axios.post(`${API}/tasks`, { description });
      setTasks([...tasks, res.data]);
      setDescription('');
      setError('');
    } catch (err) {
      setError('Erro ao adicionar tarefa.');
    }
  };

  const excluirTarefa = async (id) => {
    try {
      await axios.delete(`${API}/tasks/${id}`);
      setTasks(tasks.filter((task) => task.id !== id));
      setError('');
    } catch (err) {
      setError('Erro ao excluir tarefa.');
    }
  };

  return (
    <div style={{ padding: '2rem', fontFamily: 'Arial', maxWidth: 600, margin: '0 auto' }}>
      <h1>Trabalho APAW</h1>

      <div style={{ marginBottom: '1rem' }}>
        <input
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Digite a tarefa"
          style={{ marginRight: '0.5rem', padding: '0.5rem', width: '70%' }}
        />
        <button onClick={adicionarTarefa} style={{ padding: '0.5rem' }}>
          Adicionar
        </button>
      </div>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      <table border="1" cellPadding="8" style={{ width: '100%', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>#</th>
            <th>Descrição</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length === 0 ? (
            <tr>
              <td colSpan="3" style={{ textAlign: 'center' }}>Nenhuma tarefa cadastrada</td>
            </tr>
          ) : (
            tasks.map((task, index) => (
              <tr key={task.id}>
                <td>{index + 1}</td>
                <td>{task.description}</td>
                <td>
                  <button onClick={() => excluirTarefa(task.id)}>Excluir</button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

export default App;
