import React, { useState, useEffect } from 'react';
import axios from 'axios';

const API = 'http://localhost:5000';

function App() {
  const [tasks, setTasks] = useState([]);
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [carregando, setCarregando] = useState(false);

  useEffect(() => {
    carregarTarefas();
  }, []);

  const carregarTarefas = async () => {
    try {
      const res = await axios.get(`${API}/tasks`);
      setTasks(res.data);
      setError('');
    } catch (err) {
      console.error(err);
      setError('Erro ao carregar tarefas. Verifique a conexão com o servidor.');
    }
  };

  const adicionarTarefa = async () => {
    if (!description.trim()) return;

    setCarregando(true);
    try {
      const res = await axios.post(`${API}/tasks`, { description });
      setTasks(prev => [...prev, res.data]);
      setDescription('');
      mostrarMensagem('Tarefa adicionada com sucesso!');
    } catch (err) {
      console.error(err);
      setError('Erro ao adicionar tarefa.');
    } finally {
      setCarregando(false);
    }
  };

  const excluirTarefa = async (id) => {
    try {
      await axios.delete(`${API}/tasks/${id}`);
      setTasks(prev => prev.filter(task => task.id !== id));
      mostrarMensagem('Tarefa excluída!');
    } catch (err) {
      console.error(err);
      setError('Erro ao excluir tarefa.');
    }
  };

  const mostrarMensagem = (msg) => {
    setMensagem(msg);
    setTimeout(() => setMensagem(''), 3000);
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.titulo}>Trabalho APAW</h1>

      <div style={styles.inputContainer}>
        <input
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Digite a tarefa"
          style={styles.input}
        />
        <button
          onClick={adicionarTarefa}
          disabled={carregando}
          style={{
            ...styles.botaoAdicionar,
            cursor: carregando ? 'not-allowed' : 'pointer',
            opacity: carregando ? 0.7 : 1,
          }}
        >
          {carregando ? 'Adicionando...' : 'Adicionar'}
        </button>
      </div>

      {mensagem && <p style={styles.mensagemSucesso}>{mensagem}</p>}
      {error && <p style={styles.mensagemErro}>{error}</p>}

      <table style={styles.tabela}>
        <thead>
          <tr style={styles.cabecalho}>
            <th style={styles.celula}>#</th>
            <th style={styles.celula}>Descrição</th>
            <th style={styles.celula}>Ações</th>
          </tr>
        </thead>
        <tbody>
          {tasks.length === 0 ? (
            <tr>
              <td colSpan="3" style={styles.semTarefas}>
                Nenhuma tarefa cadastrada.
              </td>
            </tr>
          ) : (
            tasks.map((task, index) => (
              <tr key={task.id}>
                <td style={styles.celula}>{index + 1}</td>
                <td style={styles.celula}>{task.description}</td>
                <td style={styles.celula}>
                  <button
                    onClick={() => excluirTarefa(task.id)}
                    style={styles.botaoExcluir}
                  >
                    Excluir
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
}

const styles = {
  container: {
    padding: '2rem',
    fontFamily: 'Segoe UI, sans-serif',
    maxWidth: 700,
    margin: '0 auto',
  },
  titulo: {
    textAlign: 'center',
    color: '#333',
  },
  inputContainer: {
    marginBottom: '1rem',
    display: 'flex',
    gap: '0.5rem',
  },
  input: {
    padding: '0.5rem',
    flex: 1,
    border: '1px solid #ccc',
    borderRadius: '4px',
  },
  botaoAdicionar: {
    padding: '0.5rem 1rem',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
  },
  mensagemSucesso: {
    color: 'green',
    marginBottom: '1rem',
  },
  mensagemErro: {
    color: 'red',
    marginBottom: '1rem',
  },
  tabela: {
    width: '100%',
    borderCollapse: 'collapse',
    boxShadow: '0 0 10px rgba(0,0,0,0.1)',
  },
  cabecalho: {
    backgroundColor: '#f0f0f0',
  },
  celula: {
    padding: '8px',
    border: '1px solid #ccc',
  },
  botaoExcluir: {
    backgroundColor: '#dc3545',
    color: 'white',
    border: 'none',
    padding: '5px 10px',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  semTarefas: {
    textAlign: 'center',
    padding: '1rem',
    fontStyle: 'italic',
    color: '#555',
  },
};

export default App;
